<style lang="scss" scoped>
.wheel {
    animation: rotate 600ms linear infinite;
    &__left {
        transform-origin: 31px 148px;
    }
    &__right {
        transform-origin: 133px 148px;
    }
}

.digger {
    animation: up-down 800ms ease-in-out infinite alternate;
}

@keyframes up-down {
    from {
        translate: 0 0;
    } to {
        translate: 0 5px;
    }
}

@keyframes rotate {
    from {
        rotate: 0deg;
    } to {
        rotate: 360deg;
    }
    
}
</style>